public class Leetcode1189 {
  public static void main(String[] args) {
    // System.out.println(maxNumberOfBalloons("nlaebolko"));
    // System.out.println(maxNumberOfBalloons("loonbalxballpoon"));
    // System.out.println(maxNumberOfBalloons("leetcode"));
    // System.out.println(maxNumberOfBalloons("balon"));
    System.out.println(maxNumberOfBalloons(""));
  }

  public static int maxNumberOfBalloons(String text) {

    char[] chars = text.toCharArray();
    var ans = Integer.MAX_VALUE;

    int[] c = new int[26];
    for (char o : chars) {
      c[o - 'a']++;
    }

    c['l' - 'a'] = Math.floorDiv(c['l' - 'a'],2);
    c['o' - 'a'] = Math.floorDiv(c['o' - 'a'],2);
    var str = "balloon";
    for (char c1 : str.toCharArray()) {
      // if (c1 == 'l' || c1 == 'o') {
      //   if (c[c1 - 'a'] < 2 * ans) {
      //     return 0;
      //   }
      // } else {
      System.out.printf("%s->%d\n",c1,c[c1 - 'a']);
        ans = Math.min(c[c1 - 'a'], ans);
      // }
    }
    return ans;
  }
}
